import { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';

export default (props) => {
    const { field, changeTrigger, defaultValue } = props;
    const maxLength = 5000; // Ràng buộc độ dài tối đa cho trường text
    const [current, setCurrent] = useState('');

    const validateInput = (inputValue) => {
        if (inputValue.length > maxLength) {
            alert(`Giá trị không được vượt quá ${maxLength} ký tự`);
            return false;
        }
        return true;
    };

    const fieldChangeData = (e) => {
        const { value } = e.target;
        if (validateInput(value)) {
            setCurrent(value);
            changeTrigger(field, value);
        }
    };

    useEffect(() => {
        setCurrent(defaultValue);
    }, [defaultValue]);

    return (
        <div className="w-100-pct p-1 m-t-1">
            <div>
                <span className="block text-16-px">{field.field_name}QQ</span>
            </div>
            <div className="m-t-0-5">
                <textarea
                    type="email"
                    className="p-t-0-5 p-b-0-5 p-l-1 text-16-px block w-100-pct border-1"
                    value={current}
                    placeholder=""
                    onChange={fieldChangeData}
                />
            </div>
        </div>
    );
};
