import { useState } from 'react';

export default ( props ) => {
    const { field, changeTrigger, defaultValue } = props;
    const [ current, setCurrent ] = useState(defaultValue ? defaultValue:"")
    const [error, setError] = useState({});
    const fieldChangeData = (e) => {
        const { value } = e.target;
        changeTrigger( field, value )
        setCurrent( value )

    }
    const validateField = (field, value) => {
        let errorMessage = "";
        switch (field) {
           
            case "email":
                const emailRegex = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i;
                if (!value || !emailRegex.test(value)) {
                    errorMessage = "Vui lòng nhập địa chỉ email hợp lệ";
                }
                break;
        
            default:
                break;
        }
        setError({ ...error, [field]: errorMessage });
    };
    return(
        <div className="w-100-pct p-1 m-t-1">
            <div>
                <span className="block text-16-px">{ field.field_name }5</span>
            </div>
            <div className="m-t-0-5">
                <input type="email"
                    className="p-t-0-5 p-b-0-5 p-l-1 text-16-px block w-100-pct border-1"
                    placeholder="" onChange={ fieldChangeData } onBlur={(e) => validateField("email", e.target.value)}value={ current }
                    />
            </div>
        </div>
    )
}
