// }
const openTab = (url) => {
    window.location= url;
}



const autoLabel = ( key ) => {
    switch (key) {
        case "INITIALIZING":
            return "Khởi động";
            break;
        case "STARTED":
            return "Bắt đầu";
            break;
        case "PROGRESS":
            return "Thực hiện";
            break;
        case "RELEASE":
            return "Triển khai";
            break;
        case "FINAL":
            return "Cuối cùng";
            break;
        case "COMPLETED":
            return "Hoàn thành";
            break;
        case "BUG":
            return "Lỗi";
            break;
        case "SUSPEND":
            return "Tạm dừng";
            break;
        default:
            return "Khác";
            break;
    }
}

function titleCase(str) {
    return str.toLowerCase().split(' ').map(function(word) {
        return word.replace(word[0], word[0].toUpperCase());
    }).join(' ');
}

const auto_id = () => {
    return 'id' + (new Date()).getTime();
}

const removeDuplicate = ( data ) => {

    const uniqueArray = data.filter((value, index) => {
        const _value = JSON.stringify(value);
        return index === data.findIndex(obj => {
            return JSON.stringify(obj) === _value;
        });
    });
    return uniqueArray
}

export default {
    dateGenerator, openTab, titleCase, autoLabel, auto_id, removeDuplicate
}
